insert into ClassediLaurea (num_classe, denominazione, illustrazione, area_disciplinare, gruppo_disciplinare, durata_stimata) values
('L01', 'Lauree in Beni Culturali', 'La classe di laurea in Beni Culturali è una laurea che attira soprattutto coloro che mostrano una spiccata attenzione al patrimonio culturale, alla scrittura e lettura. L’Italia, luogo ricco di beni culturali fornisce ai laureati in Beni Culturali molte possibilità di impiego.', 'Umanistico-Sociale', 'gruppo_disciplinare', 'durata_stimata'),
('L02', 'Lauree in Biotecnologie', 'La classe di laurea in Biotecnologie offre una solida preparazione biologica di base che permette un interpretazione della realtà biologica in chiave prevalentemente molecolare e cellulare, senza tralasciare gli aspetti sistemici, sia in condizioni fisiologiche che patologiche.', 'Scientifico-Tecnologica', 'gruppo_disciplinare', 'durata_stimata'),
('L03', 'Lauree in Discipline delle Arti Figurative, della Musica, dello Spettacolo e della Moda', 'La classe di laurea in Design e Discipline della Moda si prefigge lo scopo di formare un progettista o un esperto in grado di confrontarsi con le innovazioni e le dinamiche di ricerca nella moda e nel design degli interni.', 'Umanistico-Sociale', 'gruppo_disciplinare', 'durata_stimata'),
('L07', 'Lauree in Ingegneria Civile e Ambientale', 'L’obiettivo principale della classe di laurea in Ingegneria Civile ed Ambientale è quello di formare ingegneri in grado di affrontare con competenze di primo livello la progettazione e la gestione delle opere e delle infrastrutture tipiche del settore civile e di operare nel campo della salvaguardia dell’ambiente da rischi naturali ed antropici.', 'Scientifico-Tecnologica', 'gruppo_disciplinare', 'durata_stimata'),
('L09', 'Lauree in Ingegneria Industriale', 'Il corso di laurea in Ingegneria Industriale fornisce gli strumenti necessari per gestire i processi di sviluppo di nuovi prodotti industriali (materiali e macchine) e di nuove tecnologie.', 'Scientifico-Tecnologica', 'gruppo_disciplinare', 'durata_stimata'),
('L10', 'Lauree in Lettere', 'La classe di laurea in Lettere mira a fornire fornire gli strumenti metodologici, critici e disciplinari per la conoscenza della civiltà moderna, dai suoi fondamenti classici e medievali alla sua configurazione contemporanea, attraverso le necessarie conoscenze di ordine linguistico, filologico, teorico, storico, geografico.', 'Umanistico-Sociale', 'gruppo_disciplinare', 'durata_stimata'),
('L13', 'Lauree in Scienze Biologiche', 'La classe di laurea in Scienze biologiche si propone l’obiettivo di fornire agli studenti una solida conoscenza di base dei principali settori delle scienze biologiche e una buona padronanza delle metodologie e tecnologie inerenti ai relativi campi di indagine scientifica, offrendo una preparazione adeguata alla conoscenza e comprensione dei progressi scientifici e tecnologici relativi alle scienze della vita.', 'Scientifico-Tecnologica', 'gruppo_disciplinare', 'durata_stimata'),
('L16', 'Lauree in Scienze dell’Amministrazione e dell’Organizzazione', 'La classe di laurea in Scienze dell’amministrazione e dell’organizzazione mira a sviluppare competenze negli ambiti storico-politico, giuridico, statistico-economico e linguistico.', 'area_disciplinare', 'gruppo_disciplinare', 'durata_stimata'),
('L18', 'Lauree in Scienze dell’Economia e della Gestione Aziendale', 'La classe di laurea in Scienze dell’economia e della gestione aziendale intende favorire l’acquisizione di solide basi e nuove conoscenze nelle aree di riferimento economiche, aziendali, giuridiche e quantitative.', 'area_disciplinare', 'gruppo_disciplinare', 'durata_stimata'),
('L19', 'Lauree in Scienze dell’Educazione e della Formazione', 'La classe di laurea in Scienze dell’Educazione e della Formazione è finalizzato all’acquisizione di conoscenze fondamentali e di competenze metodologiche inerenti ai saperi pedagogici e metodologico-didattici.', 'area_disciplinare', 'gruppo_disciplinare', 'durata_stimata'),
('L25', 'Lauree in Scienze e Tecnologie Agrarie e Forestali', 'La classe di laurea in Scienze e Tecnologie Agrarie e Forestali prevede attività formative di base, tra cui discipline matematiche, fisiche, informatiche, statistiche, chimiche e biologiche, ed attività formative caratterizzanti.', 'Scientifico-Tecnologica', 'gruppo_disciplinare', 'durata_stimata'),
('L30', 'Lauree in Scienze e Tecnologie Fisiche', 'La classe di laurea in scienze e tecnologie fisiche prevede, tra le attività formative di base, le discipline matematiche e informatiche, chimiche e fisiche. Esso inoltre prevede, tra le attività formative caratterizzanti, le discipline sperimentali e applicative, teoriche e dei fondamenti della fisica, discipline microfisiche e della struttura della materia e discipline astrofische, geofisiche e spaziali.', 'area_disciplinare', 'gruppo_disciplinare', 'durata_stimata'),
('L33', 'Lauree in Scienze Economiche', 'La classe di laurea in Scienze Economiche ha come obiettivo la formazione di laureati che dispongano di tutta la gamma degli strumenti necessari per svolgere ruoli attivi nei processi decisionali di carattere economico o che abbiano ripercussioni di natura economica.', 'area_disciplinare', 'gruppo_disciplinare', 'durata_stimata'),
('L38', 'Lauree in Scienze Zootecniche e Tecnologie delle Produzioni Animali', 'La classe di laurea in Scienze Zootecniche e Tecnologie delle Produzioni Animali mira a formare professionisti dotati di una solida preparazione scientifica e di avanzate capacità operative nei settori dell’allevamento, della genetica e dell’alimentazione animale e dell’industria di trasformazione dei prodotti di origine animale.', 'Scientifico-Tecnologica', 'gruppo_disciplinare', 'durata_stimata'),
('L40', 'Lauree in Sociologia', 'La classe di laurea in Sociologia si propone come obiettivo quello di analizzare e comprendere i complessi fenomeni sociali che circondano ciascuno di noi.', 'area_disciplinare', 'gruppo_disciplinare', 'durata_stimata'),
('L41', 'Lauree in Statistica', 'La classe di laurea in Statistica si propone come obiettivo quello di formare studenti con skills specifiche nella gestione e analisi di dati di grandi dimensioni nell’ambito di molteplici contesti lavorativi, permettendone l’impiego in contesti nazionali e internazionali per aziende pubbliche e private.', 'Scientifico-Tecnologica', 'gruppo_disciplinare', 'durata_stimata');
